function calculateMass(mass: number): number {
  return Math.floor(mass / 3) - 2;
}

function calculateFuelOfMasses(masses: number[]): number[] {
  return masses.map((a: number): number => {
    return calculateMass(a);
  });
}

export function totalFuel(masses: number[]): number {
  return calculateFuelOfMasses(masses).reduce(
    (a: number, b: number): number => {
      return a + b;
    },
    0
  );
}

