function position() {
  const movements = str.split("");
  let floor = 0;
  let movementNumber: number = 0;
  for (let i = 0; i < movements.length; i++) {
    if (movements[i] === "(") {
      ++floor;
    } else {
      --floor;
    }
    if (floor === -1) {
      movementNumber = i;
      break;
    }
  }
  return movementNumber;
}
console.log(position());

// Challenge Day 1 (2017).

const dayOne2017Input: string =
  "";

const example: string = "91212129";

export function pairs(nums: string): number {
  const movements = nums.split("");
  let result: number = 0;
  for (let i = 0; i < movements.length - 1; i++) {
    if (movements[i] == movements[i + 1]) {
      result += parseInt(movements[i]);
    }
  }
  return result;
}
console.log(pairs(dayOne2017Input));
